// src/App.js
import React from "react";

function App() {
  return (
    <div
      style={{
        backgroundColor: "#f4f7fb",
        minHeight: "100vh",
        textAlign: "center",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        padding: "30px",
      }}
    >
      {/* Encabezado */}
      <header>
        <h1 style={{ color: "#111", marginBottom: "10px" }}>
           Digital Twins Dashboard
        </h1>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            gap: "12px",
            marginBottom: "10px",
          }}
        >
          <img
            src="/metadogame2.png"
            alt="MetaDoGame"
            style={{
              width: "60px",
              height: "60px",
              borderRadius: "12px",
              objectFit: "cover",
              boxShadow: "0 2px 8px rgba(0,0,0,0.2)",
            }}
          />
          <h2
            style={{
              color: "#007bff",
              fontWeight: "bold",
              margin: 0,
              fontSize: "1.4rem",
              letterSpacing: "1px",
            }}
          >
            MetaDoGame
          </h2>
        </div>

        <hr
          style={{
            width: "65%",
            margin: "30px auto",
            border: "1px solid #ddd",
          }}
        />
      </header>

      {/* Secci贸n de m贸dulos */}
      <main
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-start",
          gap: "30px",
          flexWrap: "wrap",
          padding: "20px",
        }}
      >
        {/* Inventarios */}
        <div
          style={{
            backgroundColor: "#fff",
            borderRadius: "12px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            padding: "25px",
            width: "280px",
            transition: "transform 0.2s",
          }}
        >
          <h3 style={{ color: "#111" }}> Inventarios</h3>
          <p style={{ color: "#444", fontSize: "0.95rem" }}>
            Gesti贸n de productos, existencias y stock en tiempo real.
          </p>
          <button
            style={{
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              borderRadius: "8px",
              padding: "10px 15px",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Ir a Inventarios
          </button>
        </div>

        {/* Recomendaciones */}
        <div
          style={{
            backgroundColor: "#fff",
            borderRadius: "12px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            padding: "25px",
            width: "280px",
            transition: "transform 0.2s",
          }}
        >
          <h3 style={{ color: "#111" }}> Recomendaciones</h3>
          <p style={{ color: "#444", fontSize: "0.95rem" }}>
            Sugerencias inteligentes basadas en datos y rendimiento.
          </p>
          <button
            style={{
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              borderRadius: "8px",
              padding: "10px 15px",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Ir a Recomendaciones
          </button>
        </div>

        {/* Log铆stica */}
        <div
          style={{
            backgroundColor: "#fff",
            borderRadius: "12px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            padding: "25px",
            width: "280px",
            transition: "transform 0.2s",
          }}
        >
          <h3 style={{ color: "#111" }}> Log铆stica</h3>
          <p style={{ color: "#444", fontSize: "0.95rem" }}>
            Control de env铆os, rutas y optimizaci贸n de entregas.
          </p>
          <button
            style={{
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              borderRadius: "8px",
              padding: "10px 15px",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Ir a Log铆stica
          </button>
        </div>
      </main>
    </div>
  );
}

export default App;
